import React from 'react';
// import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Header from './components/Header';
import TaskList from './components/TaskList';
import * as Lists from './data/todoListData';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            lists: [],
            addList: false,
        }
        this.willAddList = this.willAddList.bind(this);
        this.handleAddList = this.handleAddList.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    UNSAFE_componentWillMount() {
        const taskLists = [];

        for (const key in Lists) {
            taskLists.push(Lists[key]);
        }

        this.setState({
            lists: taskLists,
        });
    }

    handleChangeValue(event) {
        this.setState({ value: event.target.value });
    }

    willAddList() {
        this.setState({
            addList: true,
        })
    }

    handleAddList(list) {
        this.setState(prevState => {
            const addedEmpty = prevState.lists.concat({
                name: list,
                numTasks: 0,
                data: [],
            });

            return ({
                lists: addedEmpty,
                addList: false,
            })
        });
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    render() {
        const outputLists = this
                            .state
                            .lists
                            .map
                            (list => 
                                <TaskList 
                                    key={list.name} 
                                    numTasks={list.numTasks} 
                                    data={list.data} 
                                    title={list.name} />); // array of components

        return (
            <div>
                <Header handleAdd={this.willAddList}/>
                {this.state.addList ? 
                    <form onSubmit={this.handleSubmit}>
                        <input type="text" name="list" placeholder="Enter name of new list" />
                        <button type="submit" value="Submit" onClick={this.handleAddList}></button>
                    </form> 
                    :
                    null}
                <div className="bkgrnd">
                    <div className="task-view">
                        {outputLists}
                    </div>
                </div>
            </div>
        )
    }
}
