import React from 'react';
import TodoItem from './TodoItem';
import FontAwesome from 'react-fontawesome';


export default class TaskList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listKey: this.props.id,
            value: '',
            numTasks: this.props.numTasks,
            todos: this.props.data,
        }; 
        this.handleComplete = this.handleComplete.bind(this);
        this.handleAdd = this.handleAdd.bind(this);
        this.handleChangeValue = this.handleChangeValue.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.displayNewTask = this.displayNewTask.bind(this);
    }

    handleComplete(id) {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed;
                }

                return todo;
            });

            return {
                todos: updatedTodos,
            };
        });
    }

    handleChangeValue(event) {
        this.setState({ value: event.target.value });
    }

    handleAdd(task) {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.concat({
                id: prevState.numTasks + 1,
                text: prevState.value,
                completed: false,
            });

            return {
                value: '',
                numTasks: prevState.numTasks + 1,
                todos: updatedTodos,
            }
        });
    }


    handleSubmit(event) {
        event.preventDefault();
    }

    displayNewTask = React.forwardRef((props, ref) => {
        <form name="task" ref={this.state.listKey} className="new-input" onSubmit={this.handleSubmit} >
            <input 
                type="text" 
                placeholder="Enter new task..." 
                name="task" 
                onChange={this.handleChangeValue}  
                value={this.state.value} />
            <button type="submit" value="Submit" onClick={this.handleAdd}>
                <FontAwesome
                    className="super-crazy-colors"
                    name="check"
                />
            </button>
        </form>
    });


    render() {
        const todoList = this.state.todos.map(task => 
            <TodoItem 
                key={task.id} 
                task={task} 
                handleChange={this.handleComplete} 
            />);

        return (
            <div className="task-list">
                <h3>{this.props.title}</h3>
                {todoList}
                <button className="add-task" onClick={this.displayNewTask}>+ Add Task</button>
            </div>
        );
    }
}
