import React from 'react';
// import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Header from './components/Header';
import TaskList from './components/TaskList';
import * as Lists from './data/todoListData';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            numLists: 0,
            taskLists: [],
            emptyPresent: false,
        }
    }

    componentWillMount() {
        const taskLists = [];

        for (const key in Lists) {
            taskLists.push(<TaskList 
                key={key} 
                numTasks={Lists[key].numTasks} 
                data={Lists[key].data} 
                title={Lists[key].name} />);
        }

        this.setState(prevState => {
            const updatedTaskLists = prevState
                                        .taskLists
                                        .concat(
                                        <TaskList 
                                            key={key} 
                                            numTasks={Lists[key].numTasks} 
                                            data={Lists[key].data} 
                                            title={Lists[key].name} />);

            return {
                numLists: updatedTaskLists.length,
                taskLists: updatedTaskLists,
                emptyPresent: prevState.emptyPresent,
            }
        });
    }

    // addEmptyTaskList() {
    //     if (!this.state) {
    //         this.setState(prevState => {

    //         })
    //     }
    // }

    render() {
        return (
            <div>
                <Header />
                <div className="bkgrnd">
                    <div className="task-view">
                        {this.state.taskLists}
                    </div>
                </div>
            </div>
        )
    }
}
