import React from 'react';
// import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Header from './components/Header';
import TaskList from './components/TaskList';
import * as Lists from './data/todoListData';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lists: [],
        }
    }

    componentWillMount() {
        for (const key in Lists) {
            this.state.lists.concat(Lists[key]);
            console.log()
        }
    }

    componentDidMount() {
        console.log(this.state.lists);
    }

    // TODO
        // find a way to put all objects into a list in state
        // do a map/forEach of the lists state and for each, output a tasklist with every list data

    render() {
        const taskLists = []; // array of components

        // create list of components of data from each list in todoListData
        for (const key in Lists) {
            taskLists.push(<TaskList 
                                key={key} 
                                numTasks={Lists[key].numTasks} 
                                data={Lists[key].data} 
                                title={Lists[key].name} />);
        }

        return (
            <div>
                <Header />
                <div className="bkgrnd">
                    <div className="task-view">
                        {taskLists}
                    </div>
                </div>
            </div>
        )
    }
}
