import React from 'react';
import TodoItem from './TodoItem';

export default class TaskList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            numTasks: this.props.numTasks,
            todos: this.props.data,
        }; 
        this.handleComplete = this.handleComplete.bind(this);
        this.handleAdd = this.handleAdd.bind(this);
        this.handleChangeValue = this.handleChangeValue.bind(this);
    }

    handleComplete(id) {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed;
                }

                return todo;
            });

            return {
                todos: updatedTodos,
            };
        });
    }

    handleChangeValue(event) {
        this.setState({ value: event.target.value });
    }

    handleAdd(task) {
        console.log("New task: ", task);
        // this.setState(prevState => {
        //     const updatedTodos = prevState.todos.shift({
        //         id: prevState.numTasks + 1,
        //         text: task.target.value,
        //         completed: false,
        //     });

        //     return {
        //         numTasks: prevState.numTasks + 1,
        //         todos: updatedTodos,
        //     }
        // });
    }

    handleSubmit(event) {
        event.prevetDefault();
    }

    render() {
        const todoList = this.state.todos.map(task => 
            <TodoItem 
                key={task.id} 
                task={task} 
                handleChange={this.handleComplete} 
            />);

        console.log(this.state);

        // const hide = {
        //     display: "none"
        // }

        return (
            <div className="task-list">
                <h3>{this.props.title}</h3>
                {todoList}
                <form name="task" className="new-input" onSubmit={this.handleSubmit} >
                {/* <form method="post" name="task" className="new-input"> */}
                    <input type="text" placeholder="Enter new task..." name="task" onChange={this.handleChangeValue}/>
                    <input type="submit" value="Submit" on></input>
                </form>
                <button className="add-task">+ Add Task</button>
            </div>
        );
    }
}
