import React from 'react';
// import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Header from './components/Header';
import TaskList from './components/TaskList';
import * as Lists from './data/todoListData';
import ReactCSSTransitionGroup from 'react-transition-group';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            lists: [],
            addList: false,
        }
        this.handleChangeValue = this.handleChangeValue.bind(this);
        this.willAddList = this.willAddList.bind(this);
        this.handleAddList = this.handleAddList.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    UNSAFE_componentWillMount() {
        const taskLists = [];

        for (const key in Lists) {
            taskLists.push(Lists[key]);
        }

        this.setState({
            lists: taskLists,
        });
    }

    handleChangeValue(event) {
        this.setState({ value: event.target.value });
    }

    willAddList() {
        this.setState({
            addList: true,
        })
    }

    handleAddList() {
        this.setState(prevState => {
            const addedEmpty = prevState.lists.concat({
                name: prevState.value,
                numTasks: 0,
                data: [],
            });

            return ({
                lists: addedEmpty,
                addList: false,
            })
        });
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    render() {
        const outputLists = this
                            .state
                            .lists
                            .map
                            (list => 
                                <TaskList 
                                    key={list.name} 
                                    numTasks={list.numTasks} 
                                    data={list.data} 
                                    title={list.name} />); 

        return (
            <div>
                <Header handleAdd={this.willAddList}/>
                {this.state.addList ? 
                        <ReactCSSTransitionGroup
                            transitionName="new-list"
                            transitionAppear={true}
                            transitionAppearTimeout={500}
                            transitionEnterTimeout={500}
                            transitionLeaveTimeout={300}>

                            <form onSubmit={this.handleSubmit}>
                                <div className="text-input">
                                    <label for="list">New List Name</label>
                                    <input 
                                        type="text" 
                                        name="list" 
                                        maxlength="20" 
                                        placeholder="Max: 20 characters" 
                                        onChange={this.handleChangeValue}/>
                                </div>

                                <button 
                                    type="submit" 
                                    value="Submit" 
                                    onClick={this.handleAddList}>Submit</button>
                            </form> 
                        </ReactCSSTransitionGroup>
                    :
                    null
                }
                <div className="bkgrnd">
                    <div className="task-view">
                        {outputLists}
                    </div>
                </div>
            </div>
        )
    }
}
